/*** 按钮方法绑定 ***/
// 主按钮
function showBtn(id) {
  let dom2 = document.getElementById('childBtnTwo')
  let dom3 = document.getElementById('childBtnThree')
  let dom4 = document.getElementById('childBtnFour')
  switch (id) {
    case 'childBtnOne':
      dom2.style.display = 'none'
      dom3.style.display = 'none'
      dom4.style.display = 'none'
      activeItem('btn_one', 'listActive')
      // 飞行导览
      clearRecording();
      window.mapApi.removeFire();
      if (!buildMarker) { // 厂区总览标签不存在，添加标签
        // 添加标签，视角飞行
        window.mapApi.addBuildMarker(locations, buildMarkerData, factoryScaleZ, (group) => {
          if (group) {
            buildMarker = group;
          }
        });
        factoryScaleZ = false;
      }
      break;
    case 'childBtnTwo':
      dom2.style.display = 'block'
      dom3.style.display = 'none'
      dom4.style.display = 'none'
      activeItem('btn_two', 'listActive')
      break;
    case 'childBtnThree':
      dom2.style.display = 'none'
      dom3.style.display = 'block'
      dom4.style.display = 'none'
      activeItem('btn_three', 'listActive')
      break;
    case 'childBtnFour':
      dom2.style.display = 'none'
      dom3.style.display = 'none'
      dom4.style.display = 'block'
      activeItem('btn_four', 'listActive')
      break;
    default:
      break;
  };
}

// 副按钮
function bindFunc(id) {
  switch (id) {
    case 'location': //人员定位
      {
