// 使用效果前清除已有效果
function clearRecording() {

  if (buildMarker) { // 清楚建筑标注
    buildMarker.remove()
    buildMarker = null
  }
  // 移除建筑透明和人员
  window.mapApi.removeWorker();

  // 移除人员巡检
  window.mapApi.removeInspection();

  // 移除巡检路线
  window.mapApi.removeViewLine();

  // 移除非法闯入效果
  window.mapApi.removeTrespass();

  // 移除消防栓效果
  window.mapApi.removeFireFight()

  // 清除救援路径
  window.mapApi.removeRescueLine()

  // 清除逃生路径
  window.mapApi.removeEacapePaths()

  // 移除重点设备
  window.mapApi.removeImporDevice()

  // 移除监控
  window.mapApi.removeVideo()

  // 移除热力图
  window.mapApi.removeTemperature()

  // 移除管道效果
  window.mapApi.removePipe()
}

// 选中状态
function activeItem(id, className) {
  const preSelectEle = document.querySelector('.' + className);
  if (preSelectEle) {
    preSelectEle.classList.remove(className);
  }

  const selectEle = document.getElementById(id)
  if (selectEle) {
    selectEle.classList.add(className);
  }
}

