function changeViewers(locations) {
  let count = 0;
  changeViewer(locations[count])

  let moveFunc = function () {
    if (count === locations.length - 1) {
      map.off('moveend', moveFunc);
      return
    }
    changeViewer(locations[count++])
  };

  if (count < locations.length - 1) {
    map.on('moveend', moveFunc);
  }
}
function changeViewer(location) {
  map.flyTo({
    ...location,
  });
}

// 飞行视角
function fly(location) {
  map.flyTo({
    ...location,
  });
}


// 建筑标注
function creatBuildMDom(text) {
  const container = document.createElement('div');
  container.className = "markerBox";
  container.style.marginTop = "-63.3px"
  const textDom = document.createElement('div');
  textDom.className = "textBox";
  textDom.innerHTML = text;
  container.appendChild(textDom);
  const imageDom = document.createElement('div');
  imageDom.className = "imageBox";
  container.appendChild(imageDom);
  return container

}

// 消防设备标签dom
function createfireMartker(id, imageUrl = "./assets/images/device.png") {
  let container = document.createElement('div');
  container.setAttribute('id', id);
  container.className = "markerDevice";
  container.style.width = "30px";
  container.style.height = "40px";
  container.style.backgroundImage = "url(" + imageUrl + ")";
  container.style.backgroundRepeat = "no-repeat";
  container.style.backgroundSize = "100% 100%";
  container.style.margin = "0px";
  container.style.backgroundPosition = "center 0";
  return container;
}

